import React, { useEffect, useState } from "react";
import "./pages.css";
import {TabList, Tab, Widget, Tag, Table, Form} from "web3uikit";


const Home = () => {
 

  

 

  return (
    <>
      <div className="content">
        <TabList defaultActiveKey={1} tabStyle="bulbUnion">
          <Tab tabKey={1} tabName="DAO">
          <div className="tabContent">
            Governance Overview
            <div className="widgets">
            <Widget info={52} title = "Proposals Creted" style={{width:"200%"}}>
              <div className="extraWidgetInfo">
                <div className="extraTitle">Pass Rate</div>
                <div className="progress">
                  <div className="progressPercentage" style={{width:`${60}%`}}></div>
                </div>
              </div>
            </Widget>
            <Widget info={423} title = "Eligible Voters"></Widget>
            <Widget info={5} title = "Ongoing Proposals"></Widget>
            </div>
            Recent Proposals
              <div style={{ marginTop: "30px" }}>
                <Table
                  columnsConfig="10% 70% 20%"
                  data={proposals}
                  header={[
                    <span>ID</span>,
                    <span>Description</span>,
                    <span>Status</span>,
                  ]}
                  pageSize={5}
                />
              </div>
            </div>
          </Tab>
          <Tab tabKey={2} tabName="Forum"></Tab>
          <Tab tabKey={3} tabName="Docs"></Tab>
        </TabList>
      </div>
    </>
  );
};

export default Home;
